    private static long calculateFloor(String instruction) {
        long raiseFLoors = instruction.chars()  
                          .filter(c -> c == '(')  
                          .count(); 
        
        long lowerFloors = instruction.chars()  
                          .filter(c -> c == ')')  
                          .count(); 
        
        return raiseFLoors - lowerFloors ;
    }
    
    private static int enterToBasement(String instruction){
        int floor = 0;
        
        for(int i = 0; i < instruction.length(); i++){
            
            floor += instruction.charAt(i) == '(' ? 1 : -1;
            
            if (floor == -1){
                return i+1;
            }
        }
        
        return floor;//default result if process never is -1
    }
